package gitRepoDemo1;

public class TestTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 2;
		System.out.println("Printing Table of "+num+": ");
		for(int i = 1; i<=10; i++)
		{
			System.out.println(num+" * "+i+" = "+(num*i));
		}

	}

}
